import React from 'react'
import { Card, Popover, Button, Avatar } from 'antd'
import { RetwwetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons'
import { useSelector } from 'react-redux'

const PostCard = ({ post }) => {
    const id = useSelector((state) => state.user.me?.id)
    return (
        <div>
            <Card
                cover={post.Image[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetwwetOutlined key="retweet" />,
                    <HeartOutlined key="heart" />,
                    <MessageOutlined key="comment" />,
                    <Popover key="more" content={(
                        <Button.Group>
                            { id && post.User.id === id ? (
                                <>
                                    <Button>수정</Button>
                                    <Button>삭제</Button>
                                </>
                            ) : <Button>신고</Button> }
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
            >
                <Image />
                <Card.Meta
                    avatar={<Avatar>{post.nickname}</Avatar>}
                    title={post.User.id}
                    description={post.content}
                />
                <Buttons />
            </Card>
            {/* <CommentForm></CommentForm>
            <Coments></Coments> */}
        </div>
    )
}

export default PostCard
