import React, { useCallback, useEffect, useState } from 'react';
import { Form, Input, Button } from 'antd';
import Link from 'next/dist/client/link';
import styled from 'styled-components'
import { useDispatch, useSelector } from 'react-redux';
import { loginRequestAction } from '../reducers/user';

const LoginForm = () => {
    const dispatch = useDispatch()
    const { logInLoading, logInErr } = useSelector((state) => state.user)

    const [email, setEmail] = useState("")

    const [password, setPassword] = useState("")

    // useEffect(()=>{
    //     if(logInErr){
    //         alert(logInErr)
    //     }
    // },[logInErr])

    const onChangeEmail = useCallback((e) => {
        setEmail(e.target.value);
    }, []);

    const onChangePw = useCallback((e) => {
        setPassword(e.target.value);
    }, []);

    const onSubmitForm = useCallback(() => {
        dispatch(loginRequestAction({ email, password }))
        if(logInErr){
            alert(logInErr)
        }
    }, [email, password, logInErr])

    return (
        <Form onFinish={onSubmitForm}>
            <div>
                <label htmlFor="user-email">이메일</label>
                <br />
                <Input type="email" name='user-email' value={email} onChange={onChangeEmail} />
            </div>
            <div>
                <label htmlFor="user-password">비밀번호</label>
                <br />
                <Input type="password" name='user-password' value={password} onChange={onChangePw} required />
            </div>
            <ButtonWrapper>
                <Button type="primary" htmlType="submit" loading={logInLoading}>로그인</Button>
                <Link href="/signup"><Button>회원가입</Button></Link>
            </ButtonWrapper>
        </Form>
    )
}

const ButtonWrapper = styled.div`
    margin-top: 10px;
`

export default LoginForm
