import React from 'react'

const PostCardContent = ({ postData }) => {
    return (
        <div>
            {postData.split(/(#[^\s#]+)/g).map((item) => {
                if (item.math(/(#[^\s#]+)/)) {
                    return <Link href={`/hashtag/${item.slice(1)}`}>{item}</Link>
                }
                return item
            })}
        </div>
    )
}

export default PostCardContent
