import React from 'react'
import { useState } from 'react'
import { useCallback } from 'react'
import { PlusOutlined } from '@ant-design/icons'

const PostImages = ({ images }) => {
  const [showImagesZoom, setShowImagesZopm] = useState(false)
  const onZoom = useCallback(() => {
    setShowImagesZopm(true)
  }, [])
  if (images.length === 1) {
    return (
      <>
        <img role="presentation" src={images[0].src} alt={images[0].src} onClick={onZoom} />
      </>
    )
  }
  if (images.length === 2) {
    return (
      <>
        <img role="presentation" width='50%' src={images[0].src} alt={images[0].src} onClick={onZoom} />
        <img role="presentation" width='50%' src={images[1].src} alt={images[0].src} onClick={onZoom} />
      </>
    )
  }
  return (
    <>
      <div>
        <img role="presentation" src={images[0].src} alt={images[0].src} onClick={onZoom} />
        <div
          role="presentation"
          style={{ display: 'inline-block', widht: "50%", textAlign: 'center', verticalAlign: "middle" }}
          onClick={onZoom}
        ></div>
        <PlusOutlined />
        <br />
        {images.length - 1}
        개의 사진 더보기
      </div>
    </>
  )
}

export default PostImages
