export const initialState = {
    isloggingIn: false,
    isLoggedIn: false,
    isLogginOut: false,
    me: null,
    signUpData: {},
    loginData: {},
}

//로그인 액션

export const loginRequestAction = (data) => {
    return {
        type: 'LOG_IN_REQUSET',
        data,
    }
}

//로그아웃 액션

export const logoutRequestAction = (data) => {
    return {
        type: 'LOG_OUT_REQUSET',
        data,
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'LOG_IN_REQUSET':
            return {
                ...state,
                isLoggingIn: true,
            }
        case 'LOG_IN_SUCCESS':
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: true,
                me: action.data
            }
        case 'LOG_IN_FAILURE':
            return {
                ...state,
                isLoggingIn: false,
                isLoggedIn: false,
            }
        case 'LOG_OUT_REQUSET':
            return {
                ...state,
                isLogginOut: true,
            }
        case 'LOG_OUT_SUCCESS':
            return {
                ...state,
                isLogginOut: false,
                isLoggedIn: false,
                me: null,
            }
        case 'LOG_OUT_FAILURE':
            return {
                ...state,
                isLogginOut: false,
            }
        default:
            return state
    }
}

export default reducer
