import React from 'react'
import { Card, Popover , ButtonGroup, Button } from 'antd'
import { RetwwetOutlined, HeadrtOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design'
import { useSelector } from 'react-redux'

const PostCard = ({ post }) => {
    const id = useSelector((state) => state.user.me?.id)
    return (
        <div>
            <Card
                cover={post.Image[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetwwetOutlined key="retweet" />,
                    <HeadrtOutlined key="heart" />,
                    <MessageOutlined key="comment" />,
                    <Popover key="more" content={(
                        <ButtonGroup>
                            {me && <Button>수정</Button>}
                            {me && <Button type="danger">삭제</Button>}
                            <Button>신고</Button>
                        </ButtonGroup>
                    )}>
                        <EllipsisOutlined />
                    </Popover>
                ]}
            >
                <Image />
                <Content />
                <Buttons />
            </Card>
            <CommentForm></CommentForm>
            <Coments></Coments>
        </div>
    )
}

export default PostCard
